INSERT INTO `drm_company_lib` VALUES ('173', '江苏新元素数字科技有限公司', null, null, '20-99人', '连云港,江苏', '互联网/移动应用/电子商务/O2O  计算机软件  电信运营/IT服务  传媒/广告/文娱', null, '江苏新元素数字科技有限公司（简称：江苏新元素、新元素数字科技、新元素），成立于2011年3月，是连云港市第一家专业从事数字动漫与软件研发相结合的新型文化科技企业。 公司的经营范围包括：创意设计，动漫产品策划、设计、制作；数字虚拟仿真项目研发；应用软件、电子商务平台研发，信息化咨询，系统集成，企业信息化项目IT项目承建；文化产业投资、运营等。\r\n企业文化理念：健康、团结、和谐、进取。我们的目标是：打造一家融合文化、科技，富于社会责任能力的现代化企业。', null, null, null, '0', null, null, null, null);
