INSERT INTO `drm_company_lib` VALUES ('175', '南京光启信息技术有限公司', null, null, '20-99人', '南京', '计算机软件', null, '南京光启信息技术有限公司南京光启信息技术有限公司是专业的移动信息化解决方案提供商，在医疗领域致力于为中国医疗行业的信息化建设以及个人健康服务提供从软件到硬件、从技术到服务的全面解决方案。依托先进的医疗管理理念和产品研发能力，打造专业、高效和便捷的医疗信息化平台，从而使医疗业务与诊断信息流通更顺畅、传递更及时、共享更快速，从而提高医疗机构的工作效率与竞争力。光启针对国内外IT领域的顶级企业提供技术顾问以及研发服务，在国内以及跨国项目合作方面有着丰富的经验，公司团队凭借强大的设计、研发、交付能力已为多个国内外知名客户提供了服务。公司良好的业绩及服务已经得到众多客户的信任与认可，为向用户提供完善的解决方案及优质的服务奠定了坚实的基础。', null, null, null, '0', null, null, null, null);
