INSERT INTO `drm_company_lib` VALUES ('211', '南京唐博科技有限责任公司', null, null, '少于50人', '南京', '计算机软件  计算机/通信/网络设备', null, '南京唐博科技有限责任公司,成立于2002年12月，是经江苏省信息产业厅正式认定的软件企业。南京唐博科技有限责任公司拥有优秀的技术力量。本公司主要从事移动通信技术在工业领域的应用，是自主进行系统设计开发、系统集成和系统服务的专业化公司。\r\n南京唐博科技有限责任公司业务范围包括移动通信技术在工业领域的应用系统，集散控制系统，生产过程系统控制的应用等。公司产品广泛应用于发电企业智能监控、电力集抄、环境保护监测、终端设备无线数据传输等多个领域。为用户提供工程设计、现场调试、应用培训及售后服务的全方位服务与支持。\r\n南京唐博科技有限责任公司拥有优秀的工程设计和技术支持队伍，公司以领先的技术，强大的设计能力，卓越的服务，在众多用户中享有较高的声望。本公司所有产品均系自主研发，并均具有自主知识产权。', null, null, null, '0', null, null, null, null);
