INSERT INTO `drm_company_lib` VALUES ('217', '南京兆康祺电子科技有限公司', null, null, '少于50人', '南京', '计算机软件  ', null, '我公司为南京兆康祺电子科技有限公司，坐落于南京市栖霞区金港科技创业园区，系南京321人才高新科技企业。公司成立于2012年3月，10月正式开始办公，主营业务是医疗信息软件开发。我们致力于为我国公立医院支付方式改革提供信息系统的技术支撑。针对此目标，我公司已成功开发了《医学编码智能信息系统》V1.0版。目前我们与北京、河北的数十家医院建立了良好的合作关系。下一步我们的主要攻关项目是在一代产品的基础上进行二代产品的研发，实现完全智能化的医学编码，提高医疗机构编码效率，降低编码成本。', null, null, null, '0', null, null, null, null);
