INSERT INTO `drm_company_lib` VALUES ('224', '南京本泰科技有限公司', null, null, '150-500人', '南京', '计算机软件  会计/咨询/外包/商业服务  ', null, '本泰科技是国内专业从事信息化软、硬件产品研发、销售、服务的软件企业和高新技术企业。本泰科技是拥有多项自主知识产权的国内领先的的企业，已经开发出了一系列软、硬件产品，相关产品获得了国家知识产权保护并通过了国家级科技成果鉴定，已被国家科技部 、国家安监总局等部委列为重点推广项目。在高科技行业、电信业、金融服务业、制造业、零售与分销业等领域积累了丰富的行业经验，具备全面的IT专业服务能力，为客户提供研究及开发、 企业解决方案、 应用软件开发和维护、 质量保证和测试等', null, null, null, '0', null, null, null, null);
