INSERT INTO `drm_company_lib` VALUES ('261', '江苏同和软件技术有限公司', null, null, '20-99人', '常州', '计算机软件', null, '我公司是注册在常州新北区创意产业基地，从事税务应用软件的开发及服务，是常州创意产业基地的骨干企业。\r\n公司于1998年组建成立，是江苏省软件企业，通过了CMMI 3级认证，经8年努力在2006年发展成为国家高新技术企业，近四年连续荣获“常州市十佳软件企业”称号，自行研发的同和系列税务应用软件产品已在全省推广。\r\n目前公司主要为江苏地区的企事业单位提供国、地税信息化管理和服务，业务稳定，因承接多项江苏省国地税应用系统的研发，后期更需推广实施，急需招贤纳士。\r\n公司营造了平等、和谐的工作氛围，公平的竞争机制，提倡务实的工作作风，树立企业个人共同进步的理念，为每位员工提供机会和平台。', null, null, null, '0', null, null, null, null);
