INSERT INTO `drm_company_lib` VALUES ('162', '南京中帕软件有限公司', '中帕', '南京中帕软件公司专业从事MASTUDIO移动平台产品开发，为电信运营商提供移动应用服务，产品广泛应用企事业单位移动应用开发和实施。Mobile Application Studio 简称“MAStudio ” ，MAStudio移动互联应用平台创建于2002年，从SMART PHONE、POCKET PC、2G时代开始研发和使用，平台的核心价值在于帮助用户能够快速、方便的实现原有业务系统到移动终端的使用。基于平台开发不仅实现移动OA、更是企业、行业信息化的综合应用开发平台。', '50-150人', '南京', '计算机软件  互联网/移动应用/电子商务/O2O ', 'http://www.mastudio.org', null, null, null, null, '0', null, null, null, null);
