INSERT INTO `drm_company_lib` VALUES ('416', '南京奇奕科技有限责任公司', null, '多媒体开发领航者奇奕科技制作团队在专业软件开发领域已有13年的历史，制作了100多种优质光碟与网路产品，内容涉及教育软件、游戏、动漫、网站设计、通用软件等等。国际市场开发先驱奇奕科技将公司产品定位以国际市场为主，制作内容的品质媲美欧美国家的产品，多次参加徳国法兰克福书展及法国、日本、香港、台湾的一些相关展览。多元化技术开发奇奕科技制作团队是在中国大陆最早投身此一领域的一群人，在电脑美术方面几乎所有类型的需求皆可制作，丰富经验在制作水准大大领先国内同行，在工程技术方面和国外的技术是同步发展。面对国际市场需求，解决各国语言及各样的问题，开发人员经验相当丰富，达到客户的需求。', '少于50人', '南京', '计算机软件', null, null, null, null, null, '0', null, null, null, null);
