INSERT INTO `drm_company_lib` VALUES ('570', '江苏税软软件科技有限公司', null, '江苏税软软件科技有限公司位于无锡国家软件园，专业从事企业电子数据增值应用的软件研发及服务，是江苏省信息产业厅认证通过的星级排行榜上的骨干软件企业，拥有自主知识产权的采集软件、查帐软件、审计软件等系列软件产品。公司于2002年起从事税务查帐软件的研发，陆续推出了查帐软件的系列产品，包括分别适用于国、地税稽查实施的税务查帐分析系统（稽查版）和适用于纳税评估的税务查帐分析系统（评估版）等工具软件。其中与无锡市国家税务局合作开发的稽查版查帐软件，于2004年9月被国家税务总局稽查局选定，目前已推广应用于全国二十个省一百多个城市三百多个稽查局，取得了很好的应用效果。且在扬州税院、江苏税校等税务培训机构建立查帐软件培训实验室（进行偷税与反偷税的博奕实验）。现公司在全国各省会城市都成立了办事处，专门为各地税务局提供技术支持。面向全国招聘优秀人才加入！', '20-99人', '无锡,贵阳,全国', '计算机软件', null, null, null, null, null, '0', null, null, null, null);
